                                    <h3>Ingredients</h3>
                                    {ingredients}
                                </div>
                                <div className="parent">
                                    <h3>Instructions</h3>
                                    <p>{this.props.Instructions}</p>
                                </div>
                            </div>
                        </div>
                        <div className="action clearfix ">
                            <a href={'/recipes/' + this.props.Id + '/edit'} className = "edit">
