var React = require('react');
var Default = require('./default');

class RecipeCards extends React.Component{
    render(){
        console.log(this.props.recipes);
        let recipeCards = this.props.recipes.map(recipe => {

            let descriptionText = recipe.description;
            descriptionText.length >= 100 ? descriptionText = recipe.description.substring(0,100) + "..." : 0;

            return (
                <div className="col-sm-6 col-md-4 col-lg-3" style={{margin: '13px 0px 13px 0px'}} key={recipe.id}>
                    <div className="card">
                        <img src={recipe.photo} className="card-img-top" alt={recipe.name} style={{width: '100%'}}/>
                        <div className="card-body">
                            <h5 className="card-title">{recipe.name}</h5>
                            <p className="card-text block-with-text">{descriptionText}</p>
                        </div>
                        <div className="card-footer">
                            <small>Recipe by: {recipe.contributor}</small>
                        </div>
                    </div>
                </div>
            )
        });
        return (
            <div className="box">
                {recipeCards}
            </div>
        )
    }
}

class Homepage extends React.Component {
    render(){
        return (
            <Default>
            <RecipeCards recipes={this.props.recipes}/>
            </Default>
        )
    }
}

module.exports = Homepage;

