                    <input name="title"/>
                    <p>Image</p>
                    <input name="img"/>
                    <p>Ingredients</p>
                    <input name="ingredients"/>
                    <p>Instructions</p>
                    <input name="instructions"/>
                    <p>--</p>
                    <input type="submit"/>
                </form>
            </div>
        </Layout>
    );
  }
}

module.exports = Form;

