var React = require('react');
class Form extends React.Component {
  render() {

    var idNum = this.props.recipes.length + 1;


    return (
      <html>
        <head>
            <link rel="stylesheet" href="https://stackpath.bootstrapcdn.com/bootstrap/4.1.3/css/bootstrap.min.css" integrity="sha384-MCw98/SFnGE8fJT3GXwEOngsV7Zt27NXFoaoApmYm81iuXoPkFOJwJ8ERdknLPMO" crossOrigin="anonymous"></link>
            <link rel="stylesheet" href="/style.css"></link>
        </head>
        <body>
          <div>
            <h1>Create Your Recipe!!</h1>
            <form method='POST' action='/recipes'>
                ID: <br/><input type='number' name='id' readonly="true" value={idNum}/><br/><br/>
                Title: <br/><input type='text' name='title' placeholder="Boiled Chicken" required/><br/><br/>
                Ingredients: <br/><input type='text' placeholder="1 Chicken" name='ingredients' required/><br/><br/>
                Instructions: <br/><textarea type='text' name='instructions' placeholder="Place chicken in water with salt. Simmer for 20 minutes" required></textarea><br/><br/>
                <input type='submit' value='Submit'/>
            </form>
          </div>
        </body>
      </html>
    );
  }
}

module.exports = Form;

